const app={init:()=>{document.addEventListener("DOMContentLoaded",app.load),console.log("DOM Loaded")},load:()=>{app.bindingInteraction()},bindingInteraction:()=>{let e=document.body.id,t=document.querySelectorAll("section")[0].id,o=document.querySelector(".content-container").querySelector(".content-form").classList[0];switch(e){case"home":case"category":case"product-detail":app.navbarInteractions();break;case"dashboard-page":app.dashboardInteractions();break;default:console.log("Log out something else")}switch(t){case"login":case"register":app.formValidation()}switch(o){case"content-form":app.formValidation(),console.log("Component Loaded")}},formValidation:()=>{document.querySelectorAll(".form-group[data-error] .form-control").forEach(e=>{e.addEventListener("input",()=>e.parentElement.removeAttribute("data-error"))})},dashboardInteractions:()=>{const e=document.querySelector(".sidebar-toggler"),t=document.querySelector("#dashboard-sidebar"),o=document.querySelector("#dashboard");e.addEventListener("click",()=>{t.classList.toggle("active"),o.classList.toggle("shrink")}),document.querySelector(".accordion-header").addEventListener("click",function(e){this.nextElementSibling.classList.toggle("active"),this.classList.toggle("active")})},navbarInteractions:()=>{const e=document.querySelector(".nav-menu-toggler"),t=document.querySelector(".sidebar");e.addEventListener("click",function(e){this.classList.toggle("active"),t.classList.toggle("active")}),document.querySelector(".dropdown-button").addEventListener("click",function(e){this.classList.toggle("active"),this.nextElementSibling.classList.toggle("active")});const o=document.querySelector(".search-toggler"),a=document.querySelector(".search-bar"),n=document.querySelector(".navbar");o.addEventListener("click",()=>{a.classList.toggle("search-bar-show"),n.classList.remove("navbar-show")}),document.querySelector(".search-box-exit").addEventListener("click",function(e){this.parentElement.parentElement.classList.remove("search-bar-show"),document.querySelector(".navbar").classList.add("navbar-show")})}};app.init();